import { Component, Input } from '@angular/core';
import { UserService } from '../../services/user.service';
import { User } from '../../models/user';

@Component({
  selector: 'app-user-login',
  templateUrl: './user-login.component.html',
  styleUrls: ['./user-login.component.css']
})
export class UserLoginComponent {
  @Input() username: string = '';
  @Input() password: string = '';



  constructor(private userService: UserService) { }

  ngOnInit(): void {

  }

  doLogin(){
    let user = this.getUser();
  }
  getUser(): any {
    this.userService.getUserByUsename(this.username).subscribe((user: User[]) => {
      return user;
    });
  }


}
<!DOCTYPE html> 
<html lang="en">
    <head>
        <meta charset="UTF-8">
        <title>Login - Ecommerce</title>
    </head>

    <body>
        <div id="form-login">
            <form class="form-group">
                <div id="username">
                    <label for="username">Username</label>
                    <br>
                    <input type="text" id="usernameText" name="username" placeholder="E-mail/CPF" [name] = "username">
                </div>
                <div id="password">
                    <label for="password">Password</label>
                    <br>
                    <input type="text" id="passwordText" name="password" [name]= "password">
                </div>
                <div id="submit">
                    <app-button [buttonText]="'Cadastrar'" [buttonDisable]="false"></app-button>
                    <app-button [inputbuttonType]="'submit'" [buttonText]="'Login'" [buttonDisable]="false" (click)="doLogin()"></app-button>
                </div>
            </form>
        </div>
    </body>
</html>
