import {Component, Input, OnInit} from '@angular/core';
import {UserService} from '../../services/user.service';
import {User} from '../../models/user';
import {FormBuilder, FormGroup, Validators} from "@angular/forms";

@Component({
    selector: 'app-user-login',
    templateUrl: './user-login.component.html',
    styleUrls: ['./user-login.component.css']
})
export class UserLoginComponent implements OnInit {
    @Input() username: string = '';
    @Input() password: string = '';
    form!: FormGroup;


    constructor(private userService: UserService, private formBuilder: FormBuilder) {


    }

    criarForm() {

        this.form = this.formBuilder.group({

            username: ['', [Validators.required]],
            senha: ['', [Validators.required]]

        })

    }


    ngOnInit(): void {
        this.criarForm();
    }

    doLogin() {
        if(this.form.invalid) return;
        let user = this.getUser();
    }

    getUser() {
        this.userService.getUserByUsename(this.username).subscribe((user: User[]) => {
            return user;
        });
    }


}
<!DOCTYPE html> 
<html lang="en">
    <head>
        <meta charset="UTF-8">
        <title>Login - Ecommerce</title>
    </head>

    <body>
        <div id="form-login">
            <form>
                <div id="username" class="form-group">
                    <label for="username">Username</label>
                    <br>
                    <input type="text" id="usernameText" name="username" placeholder="E-mail/CPF" [name] = "username">
                </div>
                <div id="password" class="form-group">
                    <label for="password">Password</label>
                    <br>
                    <input type="text" id="passwordText" name="password" placeholder="Senha" [name]= "password">
                </div>
                <div id="submit" class="form-group">
                    <app-button [buttonText]="'Cadastrar'" [buttonDisable]="false"></app-button>
                    <app-button [inputbuttonType]="'submit'" [buttonText]="'Login'" [buttonDisable]="false" (click)="doLogin()"></app-button>
                </div>
            </form>
        </div>
    </body>
</html>
